mapper.insert:
insert into t_drug(
    drugName,  specifications,unit,
     drugNote,fileurl
) values(           #{drugName}, #{specifications},#{unit},
            #{drugNote},#{fileurl}
        )

mapper.update:
update t_drug set
                  is_delete=#{isDelete}, modified_user=#{modifiedUser}, modified_time=#{modifiedTime}
where id =#{id}

mapper.select:
select count(*)
from t_drug A
where A.id=#{id}

mapper.update:
update t_drug set
    inventory=#{inventory}
where drugName =#{drugName}
